function Timer(){this.remainingTime=-1;var a=this;a.setTime=function(b){a.remainingTime=b;return a};a.getTime=function(){if(a.remainingTime>0){return a.remainingTime}else{return false}};a.decreaseTime=function(){if(a.remainingTime>0){a.remainingTime--;return true}else{return false}};a.stop=function(){clearInterval(a.runningInterval);delete a.runningInterval};a.force=function(){a.setTime(-1);a.stop()};a.delay=function(c,b){if(isDefined(c)&&(typeof c=="function")&&isDefined(b)&&($.isNumeric(b))){b=Math.floor(b);a.setTime(b);a.stop();a.runningInterval=setInterval(function(){if(a.decreaseTime()){console.warn((a.getTime()+1)+" seconds remaining...")}else{c();a.stop()}},1000)}};return a};