function Timer(){this.remainingTime=-1;var a=this;a.setTime=function(b){a.remainingTime=b;return a};a.getTime=function(){if(a.remainingTime>0){return a.remainingTime}else{return false}};a.decreaseTime=function(){if(a.remainingTime>0){a.remainingTime--;return true}else{return false}};a.delay=function(d,b){if(isDefined(d)&&(typeof d=="function")&&isDefined(b)&&($.isNumeric(b))){b=Math.floor(b);a.setTime(b);var c=setInterval(function(){console.log(a.getTime()+" seconds remaining..");if(!a.decreaseTime()){d();clearInterval(c)}},1000)}};return a};